import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function AuthorRegistration() {
  const [authorInfo, setAuthorInfo] = useState({
    name: '',
    institution: '',
    email: '',
    researchInterests: '',
    aboutYourself: '',
    password: '',
  });

  const [error, setError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setAuthorInfo({
      ...authorInfo,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Send data to backend
      const response = await axios.post('/register-author', authorInfo);
      console.log('Registration successful:', response.data.message);
      // Reset form fields after successful submission
      setAuthorInfo({
        name: '',
        institution: '',
        email: '',
        researchInterests: '',
        aboutYourself: '',
        password: '',
      });
      setError(''); // Clear any previous error message
    } catch (error) {
      if (error.response && error.response.status === 400) {
        setError(error.response.data.message); // Set the error message from the backend
      } else {
        setError('An error occurred during registration.');
      }
      console.error('Error registering author:', error);
    }
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '100vh' }}>
      <header style={{ marginBottom: '20px' }}>
        <Link to="/">
          <img src="https://patch.com/img/cdn/users/22848633/2015/10/raw/2015105617e0d11c033.png" alt="Logo" style={{ width: '50px', marginRight: '10px' }} />
        </Link>
        <p className="mb-0" style={{ fontFamily: "Liberation Mono, monospace", color: "#01428c", fontSize: "24px", fontWeight: "bold", marginLeft: "8px" }}> ScholarBase</p>
      </header>
      <div style={{ width: '500px', marginTop: '20px', marginBottom: '40px' }}>
      <h1 className="mb-4" style={{ fontFamily: "Liberation Mono, monospace", color: "#01428c", fontSize: "24px", fontWeight: "bold", textAlign: "center" }}>Author Registration</h1>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="name">Name:</label>
            <input type="text" className="form-control" id="name" name="name" value={authorInfo.name} onChange={handleChange} />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input type="password" className="form-control" id="password" name="password" value={authorInfo.password} onChange={handleChange} />
          </div>
          <div className="form-group">
            <label htmlFor="institution">Institution:</label>
            <input type="text" className="form-control" id="institution" name="institution" value={authorInfo.institution} onChange={handleChange} />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email:</label>
            <input type="email" className="form-control" id="email" name="email" value={authorInfo.email} onChange={handleChange} />
          </div>
          <div className="form-group">
            <label htmlFor="researchInterests">Research Interests:</label>
            <textarea className="form-control" id="researchInterests" name="researchInterests" value={authorInfo.researchInterests} onChange={handleChange}></textarea>
          </div>
          <div className="form-group">
            <label htmlFor="aboutYourself">About Yourself:</label>
            <textarea className="form-control" id="aboutYourself" name="aboutYourself" value={authorInfo.aboutYourself} onChange={handleChange}></textarea>
          </div>
          <button type="submit" className="btn btn-primary mt-3" onClick={handleSubmit}>Register</button>
        </form>
      </div>
    </div>
  );
}

export default AuthorRegistration;
