import React, { useState, useEffect } from 'react';
import Finder from '../apis/Finder';
import AuxNavBar from './AuxNavBar';

const Publishers = () => {
  const [publishers, setPublishers] = useState([]);

  useEffect(() => {
    const fetchPublishers = async () => {
      try {
        const response = await Finder.get('/api/publishers');
        const sortedPublishers = response.data.sort((a, b) => a.name.localeCompare(b.name));
        setPublishers(sortedPublishers);
      } catch (error) {
        console.error('Error fetching publishers:', error);
      }
    };

    fetchPublishers();
  }, []);

  return (
    <div>
      <AuxNavBar />
      <h2 style={{ textAlign: 'center' }}>Publishers</h2>
      <ul>
        {publishers.map((publisher, index) => (
          <li key={index}>
            <a href={`/publisher/${publisher.id}`} style={{ textDecoration: 'underline', fontSize: '20px', color: 'black' }}>
              {publisher.name}
            </a>
            <br />
            <span style={{ fontSize: '14px', color: 'gray' }}>{publisher.description}</span>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Publishers;
