import React, { useState, useEffect } from 'react';
import Finder from '../apis/Finder';

const CategoryList = () => {

  let [categories, setCategories] = useState([]);

  const fetchCategories = async () => {

      const res = await Finder.get('/categories');
      const result = res.data;
      setCategories(result.categories);
      console.log("this works");    
  };

  useEffect(() => {
      fetchCategories();
      console.log("this works");
  }, []);


  return (
    <div>
      <h2>Category List</h2>
      <ul>
        {categories.map((category, index) => (
          <div key={index}>
            <li>
              <a href="#" style={{ textDecoration: 'underline', fontSize: '20px' , color: 'black'}}>{category.category_name}</a>
              <br />
              <span style={{ fontSize: '8px', color: 'red' }}>{category.description}</span>
            </li>
            {/* Add a horizontal line between categories */}
            {index !== categories.length - 1 && <hr style={{ margin: '10px 0', width: '70%' }} />}
          </div>
        ))}
      </ul>
    </div>
  );
};

export default CategoryList;
